mapper.select:
select id, orderId, goodsId, qty, createTime, status, infoid
from WarehouseOut
where id = #{id,jdbcType=INTEGER}

select id, orderId, goodsId, qty, createTime, status, infoid
from WarehouseOut
where goodsId = #{goodsId,jdbcType=BIGINT}

select id, orderId, goodsId, qty, createTime, status, infoid
from WarehouseOut
where goodsId = #{goodsId,jdbcType=BIGINT}
AND orderId = #{orderId,jdbcType=BIGINT}

select w.id, w.orderId, w.goodsId, w.qty, w.createTime, w.status, w.infoid, g.picture, g.title, g.introduction
from WarehouseOut w,
Goods g
where w.goodsId=g.id
AND infoId = #{infoId,jdbcType=BIGINT}
